form:
form.addParagraph(
    'p',
    'This form data is required for the completion of an Information Technology Master Agreement.'
);

form.addTab('Document Options');

form.addRow({
    'fields': [
        {
            'type': 'select',
            'name': 'draftingStyle',
            'label': 'Drafting Style',
            'options': [
                {
                    'name': 'vendFavor',
                    'label': 'Favoring Vendor'
                },
                {
                    'selected': true,
                    'name': 'balance',
                    'label': 'Balanced between Vendor and Customer'
                },
                {
                    'name': 'custFavor',
                    'label': 'Favor Customer'
                }
            ]
        }
    ]
});

form.addRow({
    'fields': [
        {
            'type': 'text',
            'name': 'docID',
            'label': 'Document ID',
            'info': ''
        }
    ]
});
form.addRow({
    'fields': [
        {
            'type': 'date',
            'name': 'effectiveDate',
            'label': 'EffectiveDate'
        }
    ]
});

form.addTab('Parties');
form.addRow({
    'fields': [
        {
            'type': 'text',
            'name': 'custName',
            'label': 'Customer Name'
        }
    ]
});
form.addRow({
    'fields': [
        {
            'type': 'select',
            'name': 'custEntityType',
            'label': 'Entity Type',
            'options': [
                {
                    'selected': true,
                    'name': 'corporation',
                    'label': 'Corporation'
                },
                {
                    'name': 'llc',
                    'label': 'LLC'
                },
                {
                    'name': 'genPship',
                    'label': 'General Partnership'
                },
                {
                    'name': 'solePropship',
                    'label': 'Sole Proprietorship'
                }
            ]
        },
        {
            'type': 'text',
            'name': 'custStateEstab',
            'label': 'State or Province of Customer'
        }
    ]
});

form.addRow({
    'fields': [
        {
            'type': 'text',
            'name': 'vendName',
            'label': 'Vendor Name'
        }
    ]
});
form.addRow({
    'fields': [
        {
            'type': 'select',
            'name': 'vendEntityType',
            'label': 'Entity Type',
            'options': [
                {
                    'selected': true,
                    'name': 'corporation',
                    'label': 'Corporation'
                },
                {
                    'name': 'llc',
                    'label': 'LLC'
                },
                {
                    'name': 'genPship',
                    'label': 'General Partnership'
                },
                {
                    'name': 'solePropship',
                    'label': 'Sole Proprietorship'
                }
            ]
        },
        {
            'type': 'text',
            'name': 'vendStateEstab',
            'label': 'State or Province of Vendor'
        }
    ]
});

template.code:
document.addParagraph(
    'Block text',
    `This INFORMATION TECHNOLOGY MASTER AGREEMENT (this “Agreement”) is made as of ${form.getFieldValue(
        'effectiveDate'
    )} (the “Effective Date”) by and between ${
        form.getFieldRawValue('custName') === ''
            ? `______________`
            : form.getFieldValue('custName')
    } , a ${
        form.getFieldRawValue('custStateEstab') === ''
            ? `______________`
            : form.getFieldValue('custStateEstab')
    } ${form.getFieldValue('custEntityType')} ("Customer") and ${
        form.getFieldValue('vendName') === ''
            ? '_____________'
            : form.getFieldValue('vendName')
    }, a ${
        form.getFieldRawValue('vendStateEstab') === ''
            ? `______________`
            : form.getFieldValue('vendStateEstab')
    } ${form.getFieldValue(
        'vendEntityType'
    )} ("Vendor"). This Agreement consists of Parts I through III below, Attachment A, and any Addendum (as defined below) executed by the parties, including any attachments to such Addendums. `
);

document.addParagraph(
    'Block text',
    `The parties have agreed that Vendor will provide Customer with certain technology products and/or services and that Customer will pay Vendor certain fees. Therefore, in consideration of the covenants, agreements and promises set forth below, and for other good and valuable consideration, the receipt and sufficiency of which is hereby acknowledged, the parties, intending to be legally bound, hereby agree as set forth in the pages that follow.`
);

document.addParagraph(
    'Block text',
    'IN WITNESS THEREOF, the parties have executed this Agreement as of the Effective Date.'
);

document.addSignatureBlock({
    'type': 'agreement',
    'party1': {
        'type': 'company',
        'legalName': form.getFieldValue('custName')
    },
    'party2': {
        'type': 'company',
        'legalName': form.getFieldValue('vendName')
    }
});
