logic:
 //Basic hide/show logic for Addendum info
// Maintenance Plan

if (form.getFieldRawValue('maintPlanAttach') == 'maintYes') {
    form.showField('maintURL');
} else form.hideField('maintURL');
//SLA

if (form.getFieldRawValue('slaAttach') == 'slaYes') {
    form.showField('slaURL');
} else form.hideField('slaURL');
//Fee Schedule
if (form.getFieldRawValue('feeSchedule') == 'feeScheduleYes') {
    form.showField('feeScheduleURL');
} else form.hideField('feeScheduleURL');

//Project Managers
if (form.getFieldRawValue('proMgr') == 'proMgrYes') {
    form.showField('proMgrName');
} else form.hideField('proMgrName');

//Delivery Place
if (form.getFieldRawValue('deliveryPlace') == 'deliveryPlaceYes') {
    form.showField('deliveryPlaceName');
} else form.hideField('deliveryPlaceName');

//Delivery Date
if (form.getFieldRawValue('deliveryDate') == 'deliveryDateYes') {
    form.showField('dateOfDelivery');
} else form.hideField('dateOfDelivery');

// Training Plan
if (form.getFieldRawValue('training') == 'trainingPlanYes') {
    form.showField('trainingPlanURL');
} else form.hideField('trainingPlanURL');

//Other Terms
if (form.getFieldRawValue('otherTerms') == 'otherTermsYes') {
    form.showField('otherTermsDescribe');
} else form.hideField('otherTermsDescribe');


// Term of this Addendum
if (form.getFieldRawValue('termAddendum') == 'defined') {
    form.showField('definedTermDate');
} else form.hideField('definedTermDate');

