var custentitystate;

// Function below changed from getFieldValue to getFieldRawValue
switch (form.getFieldRawValue('custEntityType')) {
    case 'corporation':
        custentitystate =
            form.getFieldRawValue('custStateEstab') === ''
                ? `______________`
                : form.getFieldValue('custStateEstab') +
                  ' ' +
                  form.getFieldValue('custEntityType');
        break;
    case 'llc':
        custentitystate =
            form.getFieldRawValue('custStateEstab') === ''
                ? `______________`
                : form.getFieldValue('custStateEstab') +
                  ' ' +
                  form.getFieldValue('custEntityType');
        break;

    case 'genPship':
        custentitystate = form.getFieldValue('custEntityType');
        break;

    // This string below changed to match the "name"
    case 'solePropship':
        custentitystate = form.getFieldValue('custEntityType');
        break;
}

