        ProjectManagerNames = 'No Project Managers assigned.';
        break;

    case 'Yes':
        ProjectManagerNames = $(form.getFieldValue('proMgrName'));
}
document.addParagraph(
    'Agreement Level 1',
    `<b>Project Managers:</b><br>` + ProjectManagerNames
);

document.addParagraph('Agreement Level 1', `<b>Additional Provisions.</b>`);

if (form.getFieldRawValue('otherTerms') == 'otherTermsYes') {
    document.addParagraph(
        'Agreement Level 2',
        `${form.getFieldValue('otherTermsDescribe')}`
    );
}

if (form.getFieldRawValue('deliveryDate') == 'deliveryDateYes') {
    document.addParagraph(
        'Agreement Level 2',
        `The Delivery Date shall be ${form.getFieldValue(
            'dateOfDelivery'
        )} unless another date is agreed to by Customer in writing.`
    );
}

if (form.getFieldRawValue('deliveryPlace') == 'deliveryPlaceYes') {
    document.addParagraph(
        'Agreement Level 2',
        `Place of Delivery shall be ${form.getFieldValue('deliveryPlaceName')}.`
    );
} else document.addParagraph('Agreement Level 1 Continue', `[Reserved]`);

//Using switch again, but adding and embedding text with values from the FieldValue

var addendumTerminates;

switch (form.getFieldRawValue('termAddendum')) {
    case 'perpetual':
        addendumTerminates =
            'is perpetual, and will terminate only pursuant to terms of the Agreement';
        break;

    case 'indefinite':
        addendumTerminates =
            'is indefinite, but may be modified by a later amendment to this Addendum';
        break;

    case 'definite':
        addendumTerminates =
            'shall end on ' + form.getFieldValue('definedTermDate');
}

document.addParagraph(
    'Agreement Level 1',
    '<b>Term.</b> The Term of this Addendum ' + addendumTerminates + '.'
);

document.addParagraph(
    'Body Text',
    `This Addendum is effective as of the latest date of execution set forth below.`
);
document.addParagraph('Body Text', `<b>${form.getFieldValue('custName')}`);
document.addParagraph(
    'Body Text',
    ` By _________________________________________`
);

document.addParagraph(
    'Body Text',
    `Name: ${form.getFieldValue('custSignatoryName')}`
);
document.addParagraph(
    'Body Text',
    `Title: ${form.getFieldValue('custSignatoryTitle')}`
);

document.addParagraph('Body Text', `Date: _______________`);

document.addParagraph('Body Text', `<b>${form.getFieldValue('vendName')}`);
document.addParagraph(
    'Body Text',
    ` By _________________________________________`
);
document.addParagraph(
    'Body Text',
    `Name: ${form.getFieldValue('vendSignatoryName')}`
);
document.addParagraph(
    'Body Text',
    `Title: ${form.getFieldValue('vendSignatoryTitle')}`
);
document.addParagraph('Body Text', `Date: _______________`);
